import Link from "next/link";
import "./Header.module.css";

const Header = ({ currentUser }) => {
    const [_document, set_document] = React.useState(null);
    React.useEffect(() => {
        // window is accessible here.
        console.log(document.getElementById("main-nav-mobile"));
        console.log("window.innerHeight", window.innerHeight);
    }, []);
    const links = [
        { label: "New Motors", href: "/sell" },
        { label: "Used Motors", href: "/sell" },
        { label: "Sell My Motor", href: "/sell" },
        !currentUser && { label: "Sign Up", href: "/auth/signup" },
        !currentUser && { label: "Sign In", href: "/auth/signin" },
        currentUser && { label: "Sell Tickets", href: "/tickets/new" },
        currentUser && { label: "My Orders", href: "/orders" },
        currentUser && { label: "Sign Out", href: "/auth/signout" },
    ]
        .filter((linkConfig) => linkConfig)
        .map(({ label, href }) => {
            return (
                <li key={href}>
                    <Link href={href}>
                        <a className="f6 link dib white dim mr3 mr4-ns">
                            {label}
                        </a>
                    </Link>
                </li>
            );
        });
    function initMobileMenu(toggle, menu) {
        menu.classList.add("mobile-menu-closed");
        ["click", "touchstart"].forEach(function (eventName) {
            toggle.addEventListener(eventName, function (evt) {
                evt.preventDefault();
                menu.classList.toggle("mobile-menu-open");
                menu.classList.toggle("mobile-menu-closed");
            });
        });
    }

    // var toggle = _document.getElementById("mobile-menu-toggle");
    // var menu = _document.getElementById("main-nav-mobile");
    // initMobileMenu(toggle, menu);

    return (
        <>
            <header class="pv3 ph2 relative bb">
                <div id="container" class="flex justify-between items-center">
                    <div id="logo" class="overflow-hidden">
                        <a href="#" class="link color-inherit">
                            Logo
                        </a>
                    </div>

                    <a
                        href="#0"
                        id="mobile-menu-toggle"
                        class="link color-inherit pa3 dn-l"
                    >
                        ☰ Menu
                    </a>

                    <nav id="main-nav" class="dn db-l">
                        <a href="#" class="link db dib-l pa2 pv2 color-inherit">
                            Features
                        </a>
                        <a href="#" class="link db dib-l pa2 pv2 color-inherit">
                            Pricing
                        </a>
                        <a
                            href="#"
                            class="link dib pa2 pv2 color-inherit ba br2"
                        >
                            Sign up
                        </a>
                    </nav>

                    <nav
                        id="main-nav-mobile"
                        class="dn absolute top-100 tc right-0 w-100 w-40-ns pa2 bg-moon-gray ba"
                    >
                        <a href="#" class="link db ph2 pv3 color-inherit">
                            Features
                        </a>
                        <a href="#" class="link db ph2 pv3 color-inherit">
                            Pricing
                        </a>
                        <a
                            href="#"
                            class="link db ph2 pv3 color-inherit ba br2"
                        >
                            Sign up
                        </a>
                    </nav>
                </div>
            </header>
            <main>
                <h1 class="f0 mw8 center">Hello World</h1>
            </main>
        </>
    );
};

export default Header;
