import { useState, useEffect } from "react";
import Link from "next/link";
import "./Header.module.css";

export default ({ currentUser }) => {
    const [windowWidth, setWindowWidth] = useState("");
    const [dropDownOpen, setDropDownOpen] = useState(false);

    function initMobileMenu(toggle, menu) {
        menu.classList.add("mobile-menu-closed");
        ["click", "touchstart"].forEach(function (eventName) {
            toggle.addEventListener(eventName, function (evt) {
                evt.preventDefault();
                menu.classList.toggle("mobile-menu-open");
                menu.classList.toggle("mobile-menu-closed");
            });
        });
    }

    useEffect(() => {
        var toggle = document.getElementById("mobile-menu-toggle");
        var menu = document.getElementById("main-nav-mobile");
        initMobileMenu(toggle, menu);
        setWindowWidth(window.innerWidth);

        window.addEventListener("resize", updateWindowSize);
    }, []);
    const updateWindowSize = () => {
        setWindowWidth(window.innerWidth);
    };
    const links = [
        { label: "New Motors", href: "/new" },
        { label: "Used Motors", href: "/used" },
        { label: "Sell My Motor", href: "/sell" },
        !currentUser && { label: "Sign Up", href: "/auth/signup" },
        !currentUser && { label: "Sign In", href: "/auth/signin" },
        currentUser && { label: "Sell Tickets", href: "/tickets/new" },
        currentUser && { label: "My Orders", href: "/orders" },
        currentUser && { label: "Sign Out", href: "/auth/signout" },
    ]
        .filter((linkConfig) => linkConfig)
        .map(({ label, href }) => {
            return (
                <li key={href}>
                    <Link href={href}>
                        <a
                            classNameName={`f6 link dib  dim mr3 mr4-ns ${
                                windowWidth < 750 ? "pa3 black" : "white"
                            }`}
                        >
                            {label}
                        </a>
                    </Link>
                </li>
            );
        });

    return (
        <header className="pv3 ph2 relative bb">
            <div id="container" className="flex justify-between items-center">
                <div id="logo" className="overflow-hidden">
                    <a href="#" className="link color-inherit">
                        THE MOTOR SALES
                    </a>
                </div>

                <a
                    href="#0"
                    id="mobile-menu-toggle"
                    className={`link color-inherit pa3 dn-l mobile-menu-open ${
                        dropDownOpen ? "." : ""
                    }`}
                    onClick={() => {
                        setDropDownOpen(!dropDownOpen);
                    }}
                >
                    â˜° Menu
                </a>

                <nav id="main-nav" className="dn db-l">
                    <a href="#" className="link db dib-l pa2 pv2 color-inherit">
                        Features
                    </a>
                    <a href="#" className="link db dib-l pa2 pv2 color-inherit">
                        Pricing
                    </a>
                    <a
                        href="#"
                        className="link dib pa2 pv2 color-inherit ba br2"
                    >
                        Sign up
                    </a>
                </nav>

                <nav
                    id="main-nav-mobile"
                    className="dn absolute top-100 tc right-0 w-100 w-40-ns pa2 bg-moon-gray ba"
                >
                    <a href="#" className="link db ph2 pv3 color-inherit">
                        Features
                    </a>
                    <a href="#" className="link db ph2 pv3 color-inherit">
                        Pricing
                    </a>
                    <a
                        href="#"
                        className="link db ph2 pv3 color-inherit ba br2"
                    >
                        Sign up
                    </a>
                </nav>
            </div>
        </header>
    );
};

